mapper.select:
select * from kms_role


  and id in (
  select role_id from kms_role_garden where garden_id = #{vo.gardenId}
  )

select count(0) from kms_role where role_name =  #{name} and id in (
    select role_id from kms_role_garden where garden_id = #{gardenId}
  )

    and id != #{roleId}

select * from kms_role


    and role_name like concat('%',#{vo.keyword},'%')


    and ${vo.field} like concat('%',#{vo.value},'%')


    and id in (
        select role_id from kms_role_garden where garden_id = #{vo.gardenId}
    )
