mapper.select:
select distinct ul.user_code logUCode,ul.user_name,ul.ip,ul.address logUAddress,ul.login_date, u.*,d.dep_name depName,p.pos_name posName, g.id gardenId,g.garden_name gardenName from kms_user_loginlog ul left join kms_user u on ul.user_code = u.user_code left join kms_user_role ur on u.id = ur.user_id left join kms_dep d on u.depId = d.id left join kms_pos p on p.id = u.posId left join kms_user_garden ug on u.id = ug.user_id left join kms_garden g on ug.garden_id = g.id
ul.user_code = #{vo.userCode}
and ul.user_name like concat('%',#{vo.userName},'%')
and ul.login_date >= #{vo.startTime}
and ul.login_date <= #{vo.endTime}

mapper.insert:
insert into kms_user_loginlog (user_code, user_name, ip, 
  address, login_date
  )
values (#{userCode,jdbcType=VARCHAR}, #{userName,jdbcType=VARCHAR}, #{ip,jdbcType=VARCHAR}, 
  #{address,jdbcType=VARCHAR}, #{loginDate,jdbcType=TIMESTAMP}
  )
