mapper.select:
select perm_code from kms_permission where id in(
        select perm_id from kms_role_permission where role_id in (
        select role_id from kms_user_role where user_id = #{userId}
      )
  )

select * from kms_permission where id in(
  select perm_id from kms_role_permission where role_id in (
    select role_id from kms_user_role where user_id = #{userId}
  )
) and resource_type='menu'
order by sort;

select * from kms_permission where id in(
  select perm_id from kms_role_permission where role_id = #{roleId}
)

select perm_code from kms_permission where id in(
  select perm_id from kms_role_permission where role_id = #{roleId}
)
