mapper.select:
select * from kms_classmodel as cm
inner join kms_garden as ga on cm.garden_id = ga.id
INNER JOIN kms_subject as su on cm.subject_id = su.id
INNER JOIN kms_user as us on cm.class_teacher = us.id


          and cm.garden_id = #{classmodelVo.gardenId}


          and cm.gName = #{classmodelVo.gName}


          and su.gradeName = #{classmodelVo.gradeName}


        and cm.remark = #{classmodelVo.remark}

mapper.insert:
insert into kms_classmodel (gName, subject_id, class_hour, 
  class_cost, start_date, end_date, 
  class_teacher, remark, garden_id
  )
values (#{gname,jdbcType=VARCHAR}, #{subjectId,jdbcType=BIGINT}, #{classHour,jdbcType=BIGINT}, 
  #{classCost,jdbcType=DOUBLE}, #{startDate,jdbcType=TIMESTAMP}, #{endDate,jdbcType=TIMESTAMP}, 
  #{classTeacher,jdbcType=BIGINT}, #{remark,jdbcType=VARCHAR}, #{gardenId,jdbcType=BIGINT}
  )
