mapper.select:
select * from kms_ion_record as ion INNER JOIN kms_student_record as st
on ion.student_id = st.id
inner JOIN kms_garden as gar on st.garden_id = gar.id


    st.classroom_id=#{classroomId}


   and ion.gardenId=#{gardeId}

mapper.insert:
insert into kms_ion_record (student_id, `state`, leaving_date, 
  leaving_type, leaving_cause, remark, 
  `collect`, premium, gardenId
  )
values (#{studentId,jdbcType=BIGINT}, #{state,jdbcType=INTEGER}, #{leavingDate,jdbcType=TIMESTAMP}, 
  #{leavingType,jdbcType=INTEGER}, #{leavingCause,jdbcType=INTEGER}, #{remark,jdbcType=VARCHAR}, 
  #{collect,jdbcType=DOUBLE}, #{premium,jdbcType=DOUBLE}, #{gardenid,jdbcType=INTEGER}
  )
